import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./DayNightClock.css"; // Import external CSS

export default function DayNightClock() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const hours = time.getHours();
  const isDay = hours >= 6 && hours < 18;

  const formattedTime = time.toLocaleTimeString([], {
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
  });

  const angle = (time.getHours() * 30 + time.getMinutes() * 0.5) % 360;

  return (
    <div className={`clock-container ${isDay ? "day" : "night"}`}>
      <div className="clock-wrapper">
        <div className="orbit" />

        <motion.div
          className="rotating"
          animate={{ rotate: angle }}
          transition={{ duration: 1, ease: "linear" }}
        >
          <div className={`orb ${isDay ? "sun" : "moon"}`}>
            {isDay ? "‚òÄÔ∏è" : "üåô"}
          </div>
        </motion.div>

        <div className="clock-time">{formattedTime}</div>
      </div>
    </div>
  );
}
