import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./DayNightClock.css";
import "./StarsBackground.css"; // NEW: background stars

export default function DayNightClock() {
  const [time, setTime] = useState(new Date());
  const [meteorites, setMeteorites] = useState([]);











  
  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  // Mouse tracking for parallax effect
  useEffect(() => {
    const handleMouseMove = (e) => {
      const mouseX = e.clientX;
      const mouseY = e.clientY;
      const width = window.innerWidth;
      const height = window.innerHeight;

      const percentX = (mouseX / width) - 0.5; // Horizontal movement
      const percentY = (mouseY / height) - 0.5; // Vertical movement

      // Apply parallax effect on the stars layers
      const stars1 = document.getElementById("stars");
      const stars2 = document.getElementById("stars2");
      const stars3 = document.getElementById("stars3");

      const movementSpeed = 30; // Adjust for the speed of movement

      // Apply transformations with scaling for parallax effect
      if (stars1) stars1.style.transform = `translate(${percentX * movementSpeed}px, ${percentY * movementSpeed}px)`;
      if (stars2) stars2.style.transform = `translate(${percentX * movementSpeed * 0.7}px, ${percentY * movementSpeed * 0.7}px)`;
      if (stars3) stars3.style.transform = `translate(${percentX * movementSpeed * 0.5}px, ${percentY * movementSpeed * 0.5}px)`;
    };

    // Add mousemove event listener
    window.addEventListener("mousemove", handleMouseMove);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  // Meteorite spawning logic
  useEffect(() => {
    const spawnMeteorites = () => {
      const newMeteorites = [];
      for (let i = 0; i < 5; i++) {
        const size = Math.random() * 4 + 2; // Random size between 2px and 6px
        const startX = Math.random() * 100; // Random start position X (0-100vw)
        const startY = Math.random() * 100; // Random start position Y (0-100vh)
        const speed = Math.random() * 4 + 2; // Random speed for animation

        newMeteorites.push({
          id: Math.random(),
          style: {
            width: `${size}px`,
            height: `${size}px`,
            top: `${startY}vh`,
            left: `${startX}vw`,
            animationDuration: `${speed}s`,
          },
        });
      }
      setMeteorites(newMeteorites);
    };

    // Spawn meteorites every 3 seconds
    const meteoriteInterval = setInterval(spawnMeteorites, 3000);

    return () => clearInterval(meteoriteInterval);
  }, []);



  const hours = time.getHours();
  const minutes = time.getMinutes();
  const seconds = time.getSeconds();
  const isDay = hours >= 6 && hours < 18;

  const angle = (hours * 30 + minutes * 0.5 + seconds * (0.5 / 60)) % 360;

  const formatUnit = (value) => value.toString().padStart(2, "0");

  return (
    <div className={`clock-container ${isDay ? "day" : "night"}`}>
      {/* Only show stars at night */}
      {!isDay && (
        <>
          <div id="stars"></div>
          <div id="stars2"></div>
          <div id="stars3"></div>

          {/* Meteorites */}
          {meteorites.map((meteorite) => (
            <div
              key={meteorite.id}
              className="meteorite"
              style={meteorite.style}
            />
          ))}
        </>
      )}

      <div className="clock-wrapper">
        {/* Night orbit wobble */}
        <motion.div
          className="orbit"
          animate={{
            rotate: isDay ? 0 : 10,
          }}
          transition={{
            duration: 10,
            repeat: Infinity,
            repeatType: "reverse",
            ease: "linear",
          }}
        />

        {/* Orbital rotation */}
        <motion.div
          className="rotating"
          animate={{ rotate: angle }}
          transition={{
            type: "tween",
            duration: 1,
            ease: "linear",
          }}
        >
          <motion.div
            className={`orb ${isDay ? "sun" : "moon"}`}
            animate={{ scale: [1, 1.1, 1], opacity: [0.9, 1, 0.9] }}
            transition={{
              duration: 2,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "reverse",
            }}
          />
        </motion.div>

        {/* Digital clock */}
        <div className="clock-time flex">
          {[hours, minutes, seconds].map((unit, i) => (
            <div key={i} className="time-unit">
              <motion.div
                initial={false}
                animate={{
                  scale: [1, 1.2, 1],
                }}
                transition={{
                  duration: 0.3,
                  ease: "easeInOut",
                  repeat: 1,
                  repeatType: "reverse",
                  delay: 0.1 * i,
                }}
              >
                {formatUnit(unit)}
              </motion.div>
              {i < 2 && <span className="colon">:</span>}
            </div>
          ))}
        </div>
      </div>

      {/* Show city at the bottom */}
    </div>
  );
}
