import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./DayNightClock.css";

export default function DayNightClock() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const hours = time.getHours();
  const isDay = hours >= 6 && hours < 18;
  const angle = (hours * 30 + time.getMinutes() * 0.5) % 360;

  const formatUnit = (value) => value.toString().padStart(2, "0");

  return (
    <div className={`clock-container ${isDay ? "day" : "night"}`}>
      <div className="clock-wrapper">
        <div className="orbit" />
        <motion.div
          className="rotating"
          animate={{ rotate: angle }}
          transition={{ duration: 1, ease: "linear" }}
        >
          <motion.div
            className={`orb ${isDay ? "sun" : "moon"}`}
            animate={{ scale: [1, 1.1, 1], opacity: [0.9, 1, 0.9] }}
            transition={{ duration: 2, ease: "easeInOut", repeat: Infinity, repeatType: "reverse" }}
          />
        </motion.div>

        <div className="clock-time flex">
          {[time.getHours(), time.getMinutes(), time.getSeconds()].map(
            (unit, i) => (
              <div key={i} className="time-unit">
                <motion.div
                  key={`${unit}-${i}`}
                  animate={{ scale: [1, 1.2, 1] }}  // Time units will scale up and back down
                  transition={{
                    duration: 0.3,
                    ease: "easeInOut",
                    repeat: 1,
                    repeatType: "reverse",
                  }}
                >
                  {formatUnit(unit)}
                </motion.div>
                {i < 2 && <span className="colon">:</span>}
              </div>
            )
          )}
        </div>
      </div>
    </div>
  );
}
