import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "./DayNightClock.css";

export default function DayNightClock() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const hours = time.getHours();
  const isDay = hours >= 6 && hours < 18;
  const angle = (hours * 30 + time.getMinutes() * 0.5) % 360;

  const formatUnit = (value) => value.toString().padStart(2, "0");

  return (
    <div className={`clock-container ${isDay ? "day" : "night"}`}>
      <motion.div
        className="clock-wrapper"
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        exit={{ y: -100 }}
        transition={{ duration: 1, ease: "easeOut" }}
      >
        <div className="orbit" />
        <motion.div
          className="rotating"
          animate={{ rotate: angle }}
          transition={{ duration: 1, ease: "linear" }}
        >
          <div className={`orb ${isDay ? "sun" : "moon"}`} />
        </motion.div>

        <motion.div
          className="clock-time flex"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 1, ease: "easeOut" }}
        >
          {[time.getHours(), time.getMinutes(), time.getSeconds()].map(
            (unit, i) => (
              <div key={i} className="time-unit">
                <AnimatePresence mode="wait">
                  <motion.div
                    key={`${unit}-${i}`}
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    transition={{
                      duration: i === 2 ? 0 : 0.4,
                      ease: "easeOut",
                      type: "spring",
                      stiffness: 300,
                    }}
                  >
                    {formatUnit(unit)}
                  </motion.div>
                </AnimatePresence>
                {i < 2 && <span className="colon">:</span>}
              </div>
            )
          )}
        </motion.div>
      </motion.div>
    </div>
  );
}
