import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function DayNightClock() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const hours = time.getHours();
  const isDay = hours >= 6 && hours < 18;

  const formattedTime = time.toLocaleTimeString([], {
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
  });

  const angle = (time.getHours() * 30 + time.getMinutes() * 0.5) % 360;

  return (
    <div
      className="w-full h-screen flex items-center justify-center"
      style={{
        background: isDay ? "#87ceeb" : "#0b0c2a",
        transition: "background 1s ease-in-out",
      }}
    >
      <div className="relative w-[300px] h-[300px]">
        {/* Orbit Path */}
        <div className="absolute inset-0 rounded-full border-2 border-white/20" />

        {/* Rotating Element Container */}
        <motion.div
          className="absolute top-1/2 left-1/2"
          style={{
            transform: "translate(-50%, -50%)",
          }}
          animate={{
            rotate: angle,
          }}
          transition={{ duration: 1, ease: "linear" }}
        >
          {/* Sun or Moon */}
          <div
            className={`absolute left-[1220px] top-[-20px] w-10 h-10 rounded-full shadow-lg flex items-center justify-center text-xl ${
              isDay ? "bg-yellow-400" : "bg-white"
            }`}
          >
            {isDay ? "‚òÄÔ∏è" : "üåô"}
          </div>
        </motion.div>

        {/* Digital Clock */}
        <div className="absolute top-1/2 left-1/2 text-4xl text-white font-mono tracking-wider transform -translate-x-1/2 -translate-y-1/2">
          {formattedTime}
        </div>
      </div>
    </div>
  );
}
