import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./DayNightClock.css";
import "./StarsBackground.css"; // NEW: background stars

export default function DayNightClock() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  // Mouse tracking for parallax effect
  useEffect(() => {
    const handleMouseMove = (e) => {
      const mouseX = e.clientX;
      const mouseY = e.clientY;
      const width = window.innerWidth;
      const height = window.innerHeight;

      const percentX = (mouseX / width) - 0.5;
      const percentY = (mouseY / height) - 0.5;

      // Apply parallax effect on the stars layers
      const stars1 = document.getElementById("stars");
      const stars2 = document.getElementById("stars2");
      const stars3 = document.getElementById("stars3");
      const stars4 = document.getElementById("stars4");
      const stars5 = document.getElementById("stars5");
      const stars6 = document.getElementById("stars6");
      const stars7 = document.getElementById("stars7");

      const movementSpeed = 30;

      if (stars1) stars1.style.transform = `translate(${percentX * movementSpeed}px, ${percentY * movementSpeed}px)`;
      if (stars2) stars2.style.transform = `translate(${percentX * movementSpeed * 0.7}px, ${percentY * movementSpeed * 0.7}px)`;
      if (stars3) stars3.style.transform = `translate(${percentX * movementSpeed * 0.5}px, ${percentY * movementSpeed * 0.5}px)`;
      if (stars4) stars4.style.transform = `translate(${percentX * movementSpeed * 1.5}px, ${percentY * movementSpeed * 1.5}px)`;
      if (stars5) stars5.style.transform = `translate(${percentX * movementSpeed * 0.3}px, ${percentY * movementSpeed * 0.3}px)`;
      if (stars6) stars6.style.transform = `translate(${percentX * movementSpeed * 2}px, ${percentY * movementSpeed * 2}px)`;
      if (stars7) stars7.style.transform = `translate(${percentX * movementSpeed * 1.5}px, ${percentY * movementSpeed * 1.5}px)`;
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  const hours = time.getHours();
  const minutes = time.getMinutes();
  const seconds = time.getSeconds();
  const isDay = hours >= 6 && hours < 18;

  const angle = (hours * 30 + minutes * 0.5 + seconds * (0.5 / 60)) % 360;

  const formatUnit = (value) => value.toString().padStart(2, "0");

  return (
    <div className={`clock-container ${isDay ? "day" : "night"}`}>
      {/* Only show stars at night */}
      {!isDay && (
        <>
          <div id="stars"></div>
          <div id="stars2"></div>
          <div id="stars3"></div>
          <div id="stars4"></div> {/* Add extra small stars */}
          <div id="stars5"></div> {/* Add extra large stars */}
          <div id="stars6"></div> {/* Add ultra small stars */}
          <div id="stars7"></div> {/* Add large stars */}
        </>
      )}

      <div className="clock-wrapper">
        {/* Night orbit wobble */}
        <motion.div
          className="orbit"
          animate={{
            rotate: isDay ? 0 : 10,
          }}
          transition={{
            duration: 10,
            repeat: Infinity,
            repeatType: "reverse",
            ease: "linear",
          }}
        />

        {/* Orbital rotation */}
        <motion.div
          className="rotating"
          animate={{ rotate: angle }}
          transition={{
            type: "tween",
            duration: 1,
            ease: "linear",
          }}
        >
          <motion.div
            className={`orb ${isDay ? "sun" : "moon"}`}
            animate={{ scale: [1, 1.1, 1], opacity: [0.9, 1, 0.9] }}
            transition={{
              duration: 2,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "reverse",
            }}
          />
        </motion.div>

        {/* Digital clock */}
        <div className="clock-time flex">
          {[hours, minutes, seconds].map((unit, i) => (
            <div key={i} className="time-unit">
              <motion.div
                initial={false}
                animate={{
                  scale: [1, 1.2, 1],
                }}
                transition={{
                  duration: 0.3,
                  ease: "easeInOut",
                  repeat: 1,
                  repeatType: "reverse",
                  delay: 0.1 * i,
                }}
              >
                {formatUnit(unit)}
              </motion.div>
              {i < 2 && <span className="colon">:</span>}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
