import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "./DayNightClock.css";

export default function DayNightClock() {
  const [time, setTime] = useState(new Date());
  const [prevTime, setPrevTime] = useState(new Date()); // Track previous time for animation

  useEffect(() => {
    const interval = setInterval(() => {
      setPrevTime(time); // Set previous time before updating current time
      setTime(new Date()); // Update time every second
    }, 1000);
    return () => clearInterval(interval);
  }, [time]);

  const hours = time.getHours();
  const isDay = hours >= 6 && hours < 18;
  const angle = (hours * 30 + time.getMinutes() * 0.5) % 360;

  const formatUnit = (value) => value.toString().padStart(2, "0");

  return (
    <div className={`clock-container ${isDay ? "day" : "night"}`}>
      <div className="clock-wrapper">
        <div className="orbit" />
        <motion.div
          className="rotating"
          animate={{ rotate: angle }}
          transition={{ duration: 1, ease: "linear" }}
        >
          <div className={`orb ${isDay ? "sun" : "moon"}`} />
        </motion.div>

        <div className="clock-time flex">
          {[time.getHours(), time.getMinutes(), time.getSeconds()].map(
            (unit, i) => (
              <div key={i} className="time-unit">
                <AnimatePresence>
                  <motion.div
                    key={`${unit}-${i}`}
                    initial={{ y: -30, opacity: 0 }}
                    animate={{
                      y: 0,
                      opacity: 1,
                      transition: {
                        duration: 0.4,
                        ease: "easeOut",
                        type: "spring",
                        stiffness: 300,
                      },
                    }}
                    exit={{ y: 30, opacity: 0 }}
                  >
                    {formatUnit(unit)}
                  </motion.div>
                </AnimatePresence>
                {i < 2 && <span className="colon">:</span>}
              </div>
            )
          )}
        </div>
      </div>
    </div>
  );
}
