import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "./DayNightClock.css";

export default function DayNightClock() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const hours = time.getHours();
  const minutes = time.getMinutes();
  const seconds = time.getSeconds();
  const isDay = hours >= 6 && hours < 18;

  // Smoother rotation angle using seconds
  const angle = (hours * 30 + minutes * 0.5 + seconds * (0.5 / 60)) % 360;

  const formatUnit = (value) => value.toString().padStart(2, "0");

  return (
    <div className={`clock-container ${isDay ? "day" : "night"}`}>
      <div className="clock-wrapper">
        {/* Subtle orbit wobble at night */}
        <motion.div
          className="orbit"
          animate={{
            rotate: isDay ? 0 : 10,
          }}
          transition={{
            duration: 10,
            repeat: Infinity,
            repeatType: "reverse",
            ease: "linear",
          }}
        />

        {/* Orbital rotation */}
        <motion.div
          className="rotating"
          animate={{ rotate: angle }}
          transition={{
            type: "tween",
            duration: 1,
            ease: "linear",
          }}
        >
          <motion.div
            className={`orb ${isDay ? "sun" : "moon"}`}
            animate={{ scale: [1, 1.1, 1], opacity: [0.9, 1, 0.9] }}
            transition={{
              duration: 2,
              ease: "easeInOut",
              repeat: Infinity,
              repeatType: "reverse",
            }}
          />
        </motion.div>

        {/* Digital clock display */}
        <div className="clock-time flex">
          {[hours, minutes, seconds].map((unit, i) => (
            <div key={i} className="time-unit">
              <AnimatePresence mode="wait">
                <motion.div
                  key={unit}
                  initial={{ opacity: 0, y: -5 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: 5 }}
                  transition={{
                    duration: 0.25,
                    ease: "easeOut",
                    delay: 0.1 * i,
                  }}
                >
                  {formatUnit(unit)}
                </motion.div>
              </AnimatePresence>
              {i < 2 && <span className="colon">:</span>}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
